$(document).ready(()=>{$(window).scroll(function(){if($(this).scrollTop()>100){$('.scroll_To_Top').fadeIn()}else{$('.scroll_To_Top').fadeOut()}});$('.scroll_To_Top').click(function(){$('html, body').animate({scrollTop:0},700);return false});let totalUrl=`https://coronavirus-19-api.herokuapp.com/all`;axios.get(totalUrl).then((response)=>{let cases=response.data.cases;let death=response.data.deaths;let recovered=response.data.recovered;let casesOutput=`${ cases }`;let deathOutput=`${ death }`;let recoveredOutput=`${ recovered }`;$('.totalCases').append(casesOutput);$('.deaths').append(deathOutput);$('.recovered').append(recoveredOutput)}).catch((err)=>{console.log(err)});axios.get('https://corona.lmao.ninja/countries').then((response)=>{console.log(response);let countries=response.data;console.log(countries);let output='';$.each(countries,function(index,countries){output+=`
        
            <div class="col-md-3 col-sm-12">
                     <h3 class="bg-warning" id=${countries.country }>
                   ${countries.country }
                     </h3>
                     <p>Today Cases : ${countries.todayCases }</p>
                
                     <p>Today Deaths : ${countries.todayDeaths }</p>
                     <p>Active : ${countries.active }</p>
                        <p>Case : ${countries.cases }</p>
                        <p>Critical : ${countries.critical }</p>
                    <p>
                      Death : ${countries.deaths }
                    </p>
                     <p>Recovered : ${countries.recovered }</p>
                     
                </div>
        
        `;let countryAttr=`#${countries.country }`;console.log(countryAttr);let countryList=` <li class='m-2'><a href=${ countryAttr }>${countries.country }</a></li>`;$('.countryLists').append(countryList)});$('.byCountry').html(output);$('.totalCasesCounter').each(function(){$(this).prop('Counter',0).animate({Counter:$(this).text()},{duration:4000,easing:'swing',step:function(now){$(this).text(Math.ceil(now))}})})}).catch((err)=>{console.log(err)})});$('.refresh').on('click',function(e){e.preventDefault();window.location.reload()});
